import React from 'react';
import { useState } from 'react';
import imgBar from '../images/lidbeerbar.jpeg'
import Header from '../ComponentsPage/header';
import Footer from '../ComponentsPage/footer';
import { useSelector } from 'react-redux';
import {GoLocation} from 'react-icons/go';
import {AiFillStar} from 'react-icons/ai';
import {AiOutlineStar} from 'react-icons/ai';
import data from '../Data.json';
import CatalogDataJSON from '../Catalog.json'
import '../Style/PageStyles/CatalogItemPageStyle.css';
import { selectItem } from '../features/ItemSlice';

export default function CatalogItemPage() {
    const lang = useSelector((state) => state.languages.langIS)
    const item = useSelector((state) => state.items.itemIS)
    const [dataCurrPage, SetDataCurrPage] = useState([
        CatalogDataJSON.CatalogRU[item].Name,
        CatalogDataJSON.CatalogRU[item].Location,
        CatalogDataJSON.CatalogRU[item].NumFeedBack,
        CatalogDataJSON.CatalogRU[item].Price,
        CatalogDataJSON.CatalogRU[item].Stars,
        CatalogDataJSON.CatalogRU[item].Discription,
        CatalogDataJSON.CatalogRU[item].Type,
        CatalogDataJSON.CatalogRU[item].Time
    ]);
    let stars = 4;
    let arr_stars = [<AiOutlineStar/>,<AiOutlineStar/>,<AiOutlineStar/>,<AiOutlineStar/>,<AiOutlineStar/>];
    for(let i = 0; i < stars; i++) {
        arr_stars[i] = <AiFillStar />;
    }
    switch (item) {
        case 0:  
            // dataCurrPage[0] = CatalogDataJSON.CatalogRU[0].Name,
            // dataCurrPage[1] = CatalogDataJSON.CatalogRU[0].Location,
            // dataCurrPage[2] = CatalogDataJSON.CatalogRU[0].NumFeedBack,
            // dataCurrPage[3] = CatalogDataJSON.CatalogRU[0].Price,
            // dataCurrPage[4] = CatalogDataJSON.CatalogRU[0].Stars,
            // dataCurrPage[5] = CatalogDataJSON.CatalogRU[0].Discription,
            // dataCurrPage[6] = CatalogDataJSON.CatalogRU[0].Type,
            // dataCurrPage[7] = CatalogDataJSON.CatalogRU[0].Time
            break;
        case 1:
            break;
        case 2: 
            break;
        case 3:
            break;
        case 4:
            break;
        default:
            break;
    }
    switch (lang) {
        case "en":
            dataCurrPage[0] = CatalogDataJSON.CatalogEN[item].Name,
            dataCurrPage[1] = CatalogDataJSON.CatalogEN[item].Location,
            dataCurrPage[2] = CatalogDataJSON.CatalogEN[item].NumFeedBack,
            dataCurrPage[3] = CatalogDataJSON.CatalogEN[item].Price,
            dataCurrPage[4] = CatalogDataJSON.CatalogEN[item].Stars,
            dataCurrPage[5] = CatalogDataJSON.CatalogEN[item].Discription,
            dataCurrPage[6] = CatalogDataJSON.CatalogEN[item].Type,
            dataCurrPage[7] = CatalogDataJSON.CatalogEN[item].Time
            break;
        case "ru":
            dataCurrPage[0] = CatalogDataJSON.CatalogRU[0].Name,
            dataCurrPage[1] = CatalogDataJSON.CatalogRU[0].Location,
            dataCurrPage[2] = CatalogDataJSON.CatalogRU[0].NumFeedBack,
            dataCurrPage[3] = CatalogDataJSON.CatalogRU[0].Price,
            dataCurrPage[4] = CatalogDataJSON.CatalogRU[0].Stars,
            dataCurrPage[5] = CatalogDataJSON.CatalogRU[0].Discription,
            dataCurrPage[6] = CatalogDataJSON.CatalogRU[0].Type,
            dataCurrPage[7] = CatalogDataJSON.CatalogRU[0].Time
            break;
        case "by":
            dataCurrPage[0] = CatalogDataJSON.CatalogBY[0].Name,
            dataCurrPage[1] = CatalogDataJSON.CatalogBY[0].Location,
            dataCurrPage[2] = CatalogDataJSON.CatalogBY[0].NumFeedBack,
            dataCurrPage[3] = CatalogDataJSON.CatalogBY[0].Price,
            dataCurrPage[4] = CatalogDataJSON.CatalogBY[0].Stars,
            dataCurrPage[5] = CatalogDataJSON.CatalogBY[0].Discription,
            dataCurrPage[6] = CatalogDataJSON.CatalogBY[0].Type,
            dataCurrPage[7] = CatalogDataJSON.CatalogBY[0].Time
            break;
        default:
            break;
    }
    return(
        <div>
            <Header />
            <div className="allBlock">
                <div className="genBlokItem">
                    <img className="imgCatalogItem" src={imgBar}/>
                    <div className="div">
                    <h1>Заголовок</h1>
                    <h4><GoLocation/>Лида</h4>
                    <h4>Промышленный туризм, еда</h4>
                    <div className="divInner">

                        <div className="blockInGenBlock">
                            <h3>150 BYN</h3>
                            <h3>2.5 Часа</h3>
                        </div>
                        <div className="feedbackBlock">
                            <h3>43 отзыва</h3>
                            <h3>{arr_stars}</h3>
                        </div>
                    </div>

                    <button className="ButtonItemPage">
                        <a href="/form">Заказать</a>
                    </button>
                    </div>

                </div>
                <div className="aboutCatalogItem">
                    <h2>Описание</h2>
                    <p>Ресторан при Лидском пивзаводе отличается от обычных заведений ориентированностью меню на тех, кто хочет продегустировать пиво. Чтобы оценить вкус, нужны правильные закуски. Именно по этой причине повара нашего ресторана соединили в одно целое многолетний опыт и белорусские традиции, предложив в меню блюда традиционной европейской и национальной кухни:
                        <br/>-Различные варианты стейков
                        <br/>-Свиные колбаски по-нюрнбергски с капустой тушеной квашеной
                        <br/>-Бастурма
                        <br/>-Рулька с гарниром и соусами</p>
                </div>
            </div>
            <Footer />
        </div>
    );
}
