import {AiFillStar} from 'react-icons/ai';
import {AiOutlineStar} from 'react-icons/ai';
import '../Style/catalogItemStyle.css'
import React from 'react';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { selectItem } from '../features/ItemSlice';

function CatalogItem({img, name, stars, price, time, id}) {
    //инициализация dispatch и смежных переменных
    const dispatch = useDispatch();
    const [dataCurrPage, SetDataCurrPage] = useState([
        CatalogDataJSON.CatalogRU[item].Name, //0
        CatalogDataJSON.CatalogRU[item].Location, //1
        CatalogDataJSON.CatalogRU[item].NumFeedBack, //2
        CatalogDataJSON.CatalogRU[item].Price, //3
        CatalogDataJSON.CatalogRU[item].Stars, //4
        CatalogDataJSON.CatalogRU[item].Discription, //5
        CatalogDataJSON.CatalogRU[item].Type, //6
        CatalogDataJSON.CatalogRU[item].Time //7
    ]);
    const currency = "BYN";
    let unitTime = "Часа"
    //инициализация массива пустых звезд
    let arr_stars = [
        <AiOutlineStar/>,
        <AiOutlineStar/>,
        <AiOutlineStar/>,
        <AiOutlineStar/>,
        <AiOutlineStar/>
    ];
    //заполнение массива звезд звездами в зависимости от количества
    for(let i = 0; i < stars; i++) {
        arr_stars[i] = <AiFillStar />;
    }
    return(
        <div className="catalogItem">
            <img className="catalogItemImg"
                 src={img} 
                alt="alt"/>
            <div className="NameItem">
                <h3>{name}</h3>
            </div>
            <div className="infoAboutItem">
                <p className="p1">{price} {currency}</p>
                <p className="p2">{time} {unitTime}</p>
                <p className="p3">{arr_stars}</p>
            </div>
            <button className="ButtonItem">
                <Link to="/item" 
                    onClick={() => {
                        dispatch(selectItem(id))}
                    }>
                    Заказать
                </Link>
            </button>
        </div>
    );
}

export default CatalogItem;
