import React from 'react';
import { useState } from 'react';
import { useSelector } from "react-redux";
import data from '../../Data.json'

function InputName() {
    const lang = useSelector((state) => state.languages.langIS)
    //инициализация состаяния Name с помощью useState
    let dataCurrPage = [
        data.dataPageRU[11].name
    ]
    const [name,setName] = useState("");
        //обработка языка
        switch (lang) {
            case "ru":
                dataCurrPage[0] = data.dataPageRU[10].name;
                break;
            case "en":
                dataCurrPage[0] = data.dataPageEN[10].name;
                break;
            case "by":
                dataCurrPage[0] = data.dataPageBY[10].name;
                break;
            default:
                break;
        }
    return(
        <div className="inputNameDiv">
            <input className="inputName"
             type="text" 
             placeholder="Контактное имя"
             onChange={(e) => {
                setName(e.target.value)
             }}/>
        </div>
    );
}

export default InputName;
