import React from "react";
import {useState, useEffect} from 'react'

function InputDate() {
    const [day, setDay] = useState(1);
    const [month, setMonth] = useState(1);
    const [year, setYear] = useState(2022);

    let numAllDay = 0;
    let numMonth = 12;
    let numYear = 2024;

    if(month % 2 != 0) {
        numAllDay = 31;
    }
    if (month % 2 == 0 && month != 2) {
        numAllDay = 30;
    }
    if (month == 2) {
        if (year % 2 == 0) {
            numAllDay = 28;
        }
        else{
            numAllDay = 29;
        }
    }

    const arrDay = []
    const arrMonth = []
    const arrYear = []

    for(let i = 0; i < numAllDay; i++) {
        arrDay[i] = i + 1;
    }
    for(let i = 0; i < numMonth; i++) {
        arrMonth[i] = i + 1;
    }
    for(let i = 2022; i < numYear; i++) {
        arrYear[i] = i;
    }

    const tempDays = arrDay.map((d) => 
        <option value={d}>{d}</option>
    )
    const tempMonths = arrMonth.map((m) => 
        <option value={m}>{m}</option>
    )
    const tempYears = arrYear.map((y) => 
        <option value={y}>{y}</option>
    )


    return(
        <div>
            <div className="boxForInputs">
                <label htmlFor="inputDay">День</label>
                <select className="date" name="inputDay" id="inputDay" onChange={
                    (e) => {
                        setDay(e.target.value)
                    }}>
                {tempDays}
                </select>
            </div>
            <div className="boxForInputs">
                <label htmlFor="inputMonth">Месяц</label>
                <select className="date" name="inputMonth" id="inputMonth" onChange={
                    (e) => {
                        setMonth(e.target.value)
                    }}>
                    {tempMonths}
                </select>
            </div>
            <div className="boxForInputs">
                <label htmlFor="inputYear">Год</label>
                <select className="date" name="inputYear" id="inputYear" onChange={
                    (e) => {
                        setYear(e.target.value)
                    }}>
                    {tempYears}
                </select>
            </div>

        </div>
    );
}
export default InputDate;
