import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    itemIS: 0,
}
const ItemSlice = createSlice({
    name: 'item',
    initialState,
    reducers:{
        selectItem(state, action) {
            state.itemIS = action.payload;
        },
    }
});

export const {selectItem} = ItemSlice.actions;
export default ItemSlice.reducer;
